SET SQL_MODE="NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";

CREATE DATABASE IF NOT EXISTS `chuxin` DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci;
USE `chuxin`;
DROP TABLE IF EXISTS `page`;
CREATE TABLE IF NOT EXISTS `page` (`id` int(11) NOT NULL AUTO_INCREMENT,`title` varchar(255) NOT NULL COMMENT '页面标题',`title_en` varchar(255) NOT NULL COMMENT '标题——英文',`timestamp` varchar(25) NOT NULL COMMENT '页面创建时间戳',`cdate` varchar(25) NOT NULL COMMENT '日期',`ctime` varchar(25) NOT NULL COMMENT '时间',`pagestyle` varchar(255) NOT NULL COMMENT '页面分隔，不同页面风格不同区分一些',`pagecode` mediumtext NOT NULL COMMENT '页面代码',`pagecode_en` mediumtext NOT NULL COMMENT '主页面——英文',`visit` int(11) NOT NULL COMMENT '浏览次数',`zan` int(11) NOT NULL COMMENT '点赞数量',`demo` text NOT NULL COMMENT '备注',PRIMARY KEY (`id`)) ENGINE=InnoDBDEFAULT CHARSET=utf8 COMMENT='动态页面' AUTO_INCREMENT=330 ;

DROP TABLE IF EXISTS `speak`;
CREATE TABLE IF NOT EXISTS `speak` (  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增序列',  `targetid` int(11) NOT NULL COMMENT '目标id',  `ip` varchar(50) NOT NULL COMMENT 'ip地址',  `nickname` varchar(50) NOT NULL COMMENT '昵称',  `text` mediumtext NOT NULL COMMENT '内容',  `timestamp` int(11) NOT NULL COMMENT '时间戳',  `zan` int(11) NOT NULL COMMENT '点赞',  KEY `id` (`id`)) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COMMENT='评论' AUTO_INCREMENT=48 ;
